<style type="text/css" style="display:none">html{background-color:#c0b481}body{position:relative;margin:1em auto;width:960px;background:#fff;font-family:Verdana,Arial,Helvetica,sans-serif;font-size:14px;color:#333}a.chirplink{display:none}#container{position:relative;width:960px}#header{height:122px;background:#000 url(http://www.the-art-of-web.com/images/head_01.jpg) left top;vertical-align:bottom}#footer,div.divider,h2:before{background:#000 url(http://www.the-art-of-web.com/images/divider_bg.jpg) left top;background-attachment:fixed}div.divider{clear:both;margin-top:1em;height:4px}h1{margin:1em 0 0.5em;line-height:1.2;font-size:28px;color:#5e5223}h2{position:relative;clear:both;margin:40px -10px 20px;padding:4px 8px;border-radius:6px /12px;background:#e0d8b7;box-shadow:0 0 5px #fff inset;letter-spacing:1px;font-size:18px}h2:before{display:block;position:absolute;top:-24px;margin:0 -8px;padding:0;width:100%;height:4px;content:""}h2.nodivider{margin:16px 0 1em;padding:4px 8px}h2.nodivider:before{display:none}h3{font-size:14px}h4{font-size:12px}a:link,a:visited{color:#5e5223}a:hover{text-decoration:underline!important}#content a:hover{color:#c09c81!important}a[href=#]{color:#333}a[target]{color:green}s{color:#ccc}#menu{margin:0 50px;padding:0 15px;height:25px;background-color:#000;background-color:rgba(0,0,0,0.6);border-radius:1em 1em 0 0}#menu ul{margin:0;padding:4px 12px 2px;list-style-type:none}#menu li{float:left;margin:0 6px 0 0;padding:0}#menu li:before{content:" :  ";color:#fff;text-shadow:1px 1px 1px #000}#menu li:first-child:before{content:""}#menu a:link,#menu a:visited{text-shadow:1px 1px 1px #000;text-decoration:none;font-weight:bold;font-size:12px;color:#fff}#searchbox{float:right;position:relative;margin:4px 0 0;opacity:0.9}#searchbox:hover,#suggestbox:focus{opacity:1}#sitesearch{width:150px;height:12px;font-size:12px}#suggest{position:absolute;display:none;top:20px;margin:0;padding:1px!important;min-width:160px;border:#fc3;background:#fff;box-shadow:0