function Hilitor(id,tag){var targetNode=document.getElementById(id)||document.body;var hiliteTag=tag||"EM";var skipTags=new RegExp("^(?:"+hiliteTag+"|SCRIPT|FORM|SPAN)$");var colors=["#ff6","#a0ffff","#9f9","#f99","#f6f"];var wordColor=[];var colorIdx=0;var matchRegex="";this.setRegex=function(input){input=input.replace(/^[^\w]+|[^\w]+$/g,"").replace(/[^\w'-]+/g,"|");matchRegex=new RegExp("\\b("+input+")\\b","i");}
